const dns = require('dns');
const queryDns = (domain) => {
    return new Promise((resolve, reject) => {
        dns.lookup(domain, (err, address, family) => {
            if (err) {
                reject(err);
            } else {
                resolve(address);
            }
        });
    });
}


function getRandomStr(length) {
    const str = 'abcdefghijklmnopqrstuvwxyzABCDEFGHIJKLMNOPQRSTUVWXYZ0123456789';
    let result = '';
    for (let i = 0; i < length; i++) {
        result += str.charAt(Math.floor(Math.random() * str.length));
    }
    return result;
}

function getDomainInfo(domain) {
    const dnsStatusMaps = {
        ok: "正常",
        active: "活动状态",
        inactive: "非活动状态",
        clientDeleteProhibited: "注册商禁止删除",
        clientUpdateProhibited: "注册商禁止更新",
        clientTransferProhibited: "注册商禁止转移",
        clientRenewProhibited: "注册商禁止续费",
        serverUpdateProhibited: "注册局禁止更新",
        serverDeleteProhibited: "注册局禁止删除",
        serverRenewProhibited: "注册局禁止续费",
        serverTransferProhibited: "注册局禁止转移",
        pendingTransfer: "转移中",
        redemptionPeriod: "赎回宽限期",
        pendingDelete: "域名服务商设置待删除/赎回期",
        pendingVerification: "注册信息确认中",
        clientHold: "注册商设置停止解析",
        serverHold: "注册局设置停止解析",
        addPeriod: "注册宽限期",
        autoRenewPeriod: "自动续费宽限期",
        pendingCreate: "注册中",
        pendingRestore: "恢复中",
        pendingUpdate: "更新中",
        renewPeriod: "续费宽限期",
        transferPeriod: "转移宽限期"
    };
    return new Promise((resolve, reject) => {
        const seqid = getRandomStr(16);
        fetch(`https://qcwss.cloud.tencent.com/whois/ajax?reqSeqId=${seqid}`, {
            "headers": {
                "content-type": "application/x-www-form-urlencoded; charset=UTF-8",
            },
            "body": `action=DescribeWhoisInfoSpecial&domainName=${domain}`,
            "method": "POST"
        }).then(res => {
            return res.json()
        }).then(res => {
            let whoisInfo = res?.result?.WhoisInfo;
            if (!whoisInfo) {
                throw Error(res?.result?.Error?.Message || '查询失败')
            }
            let domainInfo = {
                domain: domain,
                RegistrantEmail: whoisInfo.RegistrantEmail.join(','),
                RegistrantName: whoisInfo.RegistrantName,
                registrar: whoisInfo.RegistrarRaw,
                status: whoisInfo.DomainStatus.map(s => dnsStatusMaps[s]).join(','),
                createdTime: whoisInfo.RegistrationDate,
                updatedTime: whoisInfo.UpdateDate,
                expiresTime: whoisInfo.ExpirationDate,
                expired: whoisInfo.Expired,
                dns: whoisInfo.DnsServer.join(','),
                allinfo: nl2br(whoisInfo.Raw[0])
            };
            resolve(domainInfo)
        }).catch(e => {
            reject(e)
        });
    })
}

function getIpInfo(ip) {
    return new Promise((resolve, reject) => {
        fetch("https://api.proxy.ipip.sh/ipv4?ip=" + ip).then(
            res => {
                if (res.status === 200) {
                    return res.json();
                } else {
                    throw new Error('查询失败');
                }
            }
        ).then(res => {
            resolve(res)
        }).catch(e => {
            reject(e)
        });
    });
}

function getTopLevelDomain(domain) {
    // 定义一个列表来识别特殊的多部分顶级域名
    const specialTLDs = [
        'co.uk', 'gov.uk', 'org.uk', 'ac.uk', 'me.uk',
        'gov.cn', 'com.cn', 'org.cn', 'net.cn', 'edu.cn',
        // 在这里添加更多需要特殊处理的顶级域名
    ];

    // 分割域名为各个部分
    const parts = domain.split('.');

    // 如果域名部分少于2个，则直接返回原域名
    if (parts.length < 2) {
        return domain;
    }

    // 检查域名的最后两部分是否在特殊顶级域名列表中
    const lastTwoParts = parts.slice(-2).join('.');
    if (specialTLDs.includes(lastTwoParts) && parts.length > 2) {
        // 如果是特殊情况，返回最后三部分
        return parts.slice(-3).join('.');
    } else {
        // 否则，返回最后两部分
        return lastTwoParts;
    }
}


function main() {
    // 解析地址 获取domain
    const domain = parseURL("{{payload}}");

// 如果hostname为空，则提示错误
    if (!domain) {
        quickcommand.showMessageBox("请输入正确的域名", "error");
        return false;
    }
    // 初始化样式
    initCss();

    // 查询dns信息
    showLoading("查询中...")
    queryDns(domain).then(ip => {
        // 查询 ip 相关信息
        getIpInfo(ip).then(ipinfo => {
            // 输出ip相关内容
            console.log(renderTableDNSInfo(domain, ip))
            console.log(renderTableIpInfo(ipinfo))
            hideLoading();
        }).catch(e => {
            // 输出ip相关内容
            console.log(renderTableDNSInfo(domain, ip))
            console.error(e)
            hideLoading();
        }).finally(() => {
            // 查域名 whois 信息
            const topLevelDomain = getTopLevelDomain(domain);
            getDomainInfo(topLevelDomain).then(domainInfo => {
                console.log(renderTableWhoisInfo(domainInfo))
            }).catch(e => {
                console.error("whois信息查询失败")
                console.error(e)
            }).finally(() => {
                hideLoading()
            });
        });
    }).catch(e => {
        hideLoading()
        console.log("地址解析失败");
    })
}
function nl2br(str, isXhtml = true) {
    const breakTag = isXhtml ? '<br />' : '<br>';
    return str.replace(/\r\n|\n\r|\r|\n/g, breakTag + '$&');
}

main()
